import { Component, OnInit } from '@angular/core';
import { TitleStrategy } from '@angular/router';
import { AuthService } from 'src/app/services/auth.service';
import { ReservasAdminService } from 'src/app/services/reservas-admin.service';

@Component({
  selector: 'app-reportes',
  templateUrl: './reportes.component.html',
  styleUrls: ['./reportes.component.css']
})
export class ReportesComponent implements OnInit {

  dependencia={
    _id:'',
    id_unidad:'',
    nombre_unidad:'',
    id_tipo_unidad:'',
    tipo_unidad:''
  }

  dependencias=[this.dependencia]

  reservas=[{fini:'', 
            fend:'', 
            namevent:'', 
            user:{name:'',
                  ced:'',
                  roles:[],
                  dependencias:this.dependencias,
                  email:'',
                  telefono:''}, 
            sitio:'', 
            state:'', 
            anexo:''}]


  constructor(private reseserver:ReservasAdminService,
    private auth:AuthService) { }

  async ngOnInit():Promise<void> {
    await this.reseserver.getReservas()
    .subscribe(
      res=>{
        this.reservas=res;
      }
    )
    await this.auth.getDependencias().subscribe(
      res => {
        for (let i of Object.values(res)) {
          this.dependencias.push(i);
        }
        for (let index = 0; index < 10; index++) {
          const element = this.dependencias[index];
          this.reservas.forEach(r=>{
            console.log(element._id);
            console.log(r.user.dependencias);

          })
        }
      }
    )
  }
}
