import { Injectable } from '@angular/core';
import { HttpClient, HttpRequest, HttpEvent, HttpHandler } from '@angular/common/http';
import { Observable } from 'rxjs';
import { UserService } from './user.service';

@Injectable({
  providedIn: 'root'
})
export class ReservasAdminService {
  private URL='http://localhost:3000/api/reservas'

  constructor(private http:HttpClient,
    private ususer:UserService,
    private ch:HttpHandler) { }

  getReservas(){
    return this.http.get<any>(this.URL+'/report')
  }

  async solReserva(res:any){
    await this.http.post<any>(this.URL+'/solicitar',res).subscribe()
  }

  upload(file: File): Observable<HttpEvent<any>> {
    const formData = new FormData()

    formData.append('file',file)

    const req = new HttpRequest('POST', this.URL+'/upload', file, {
      reportProgress: true,
      responseType: 'json'
    });

    return this.http.request(req)
  }

  getFiles(): Observable<any> {
    return this.http.get(`${this.URL}/files`);
  }

  UpdateState(state:string, id:string){
    const body=new FormData()
    body.append('id',id)
    body.append('state',state)
    return this.http.put<any>(this.URL+'/resmod',body).subscribe()
  }
}
