            sitio:'', 
            state:'', 
            anexo:''}]


  constructor(private reseserver:ReservasAdminService) { }

  ngOnInit(): void {
    this.reseserver.getReservas()
    .subscribe(
      res=>{
        this.reservas=res;
        console.log(this.reservas);
      }
    )
